const Sorting = ({ key, dir, data }) => {
  let lowerKey = key.toLowerCase();
  let lowerDir = dir.toLowerCase();
  console.log(lowerKey, lowerDir);
  if (lowerKey === "name" && lowerDir === "desc") {
    return data.sort((a, b) => {
      let fa = a.name.toLowerCase();
      let fb = b.name.toLowerCase();
      if (fa < fb) return 1;
      if (fa > fb) return -1;
      return 0;
    });
  }
  if (lowerKey === "name" && lowerDir === "asc") {
    return data.sort((a, b) => {
      let fa = a.name.toLowerCase();
      let fb = b.name.toLowerCase();
      if (fa > fb) return 1;
      if (fa < fb) return -1;
      return 0;
    });
  }
  return data;
};

export default Sorting;
