import React, { useState, useEffect } from "react";
import { Link } from "react-router-dom";
import axios from "axios";

function StudentList() {
  //const [student,setStudent]=useState([]);
  const [userForm, setUserForm] = useState([]);
  const [filter, setFilter] = useState({
    name: "",
    email: "",
    rollno: "",
  });

  const deleteStudent = (_id) => {
    axios
      .delete(`http://localhost:4000/students/delete-student/${_id}`)
      .then(() => {
        console.log("Data successfully deleted!");
      })
      .catch((error) => {
        console.log(error);
      });
  };

  const onFilter = (event) => {
    setFilter({ ...filter, [event.target.name]: event.target.value });
    //onSubmitFilter();
  };

  const onSubmitFilter = () => {
    const filtered = userForm.filter(
      (user) =>
        (filter.name != "" &&
          user.name.toLowerCase().includes(filter.name.toLowerCase())) ||
        (filter.email != "" &&
          user.email.toLowerCase().includes(filter.email.toLowerCase())) ||
        (filter.rollno != "" &&
          user.rollno.toString().includes(filter.rollno.toString()))
    );
    setUserForm([...filtered]);
    console.log(filtered);
  };

  useEffect(() => {
    axios
      .get("http://localhost:4000/students/")
      .then((res) => {
        setUserForm(res.data.data);
      })
      .catch((error) => {
        console.log(error);
      });
  }, []);

  return (
    <div>
      <div className="row mb-3">
        <input
          type="text"
          className="form-control col"
          placeholder="Name"
          name="name"
          onChange={onFilter}
          value={filter.name}
        />
        &nbsp;
        <input
          type="text"
          className="form-control col"
          placeholder="Email"
          name="email"
          onChange={onFilter}
          value={filter.email}
        />
        &nbsp;
        <input
          type="number"
          className="form-control col"
          placeholder="Roll no"
          name="rollno"
          onChange={onFilter}
          value={filter.rollno}
        />
        &nbsp;
        <button className="btn btn-primary col" onClick={onSubmitFilter}>
          Submit
        </button>
      </div>
      <table className="table">
        <thead>
          <tr>
            <th scope="col">#</th>
            <th scope="col">Name</th>
            <th scope="col">Email</th>
            <th scope="col">Roll no</th>
            <th scope="col">Action</th>
          </tr>
        </thead>
        <tbody>
          {userForm.map((user, index) => {
            return (
              <tr key={index}>
                <th scope="row">{user._id}</th>
                <td>{user.name}</td>
                <td>{user.email}</td>
                <td>@{user.rollno}</td>
                <td>
                  <Link
                    className="btn btn-primary btn-sm me-2"
                    to={"/edit-student/" + user._id}
                  >
                    Edit
                  </Link>
                  <button
                    className="btn btn-danger btn-sm"
                    onClick={() => deleteStudent(user._id)}
                  >
                    Delete
                  </button>
                </td>
              </tr>
            );
          })}
        </tbody>
      </table>
    </div>
  );
}

export default StudentList;
