import React, { useState, useEffect } from "react";
import { Link } from "react-router-dom";
import axios from "axios";
function StudentList() {
  const [userForm, setUserForm] = useState([]);
  const [data, setData] = useState([]);
  const [isFilter, setIsFilter] = useState(false);
  const [filter, setFilter] = useState({
    name: "",
    email: "",
    rollno: "",
  });

  const deleteStudent = (_id) => {
    axios
      .delete(`http://localhost:4000/students/delete-student/${_id}`)
      .then(() => {
        console.log("Data successfully deleted!");
      })
      .catch((error) => {
        console.log(error);
      });
  };

  const onKeyChange = (event) => {
    const currefilter = { ...filter, [event.target.name]: event.target.value };
    setFilter(currefilter);
    console.log(currefilter);
  };

  const onFilter = (filters) => {
    if (
      filters.name.trim() === "" &&
      filters.email.trim() === "" &&
      filters.rollno.trim() === ""
    ) {
      setUserForm(data);
      return;
    }
    const fitleredData = data.filter((item) =>
      item.name.toLowerCase().includes(filters.name.toLowerCase())
    );
  };

  const fetchStudents = () => {
    axios
      .get("http://localhost:4000/students/")
      .then((res) => {
        setUserForm(res.data.data);
        setData(res.data.data);
      })
      .catch((error) => {
        console.log(error);
      });
  };

  useEffect(() => {
    fetchStudents();
  }, []);

  return (
    <div>
      <div className="row">
        <input
          type="text"
          className="form-control col"
          placeholder="Name"
          name="name"
          onChange={onKeyChange}
        />
        &nbsp;
        <input
          type="text"
          className="form-control col"
          placeholder="Email"
          name="email"
          onChange={onKeyChange}
        />
        &nbsp;
        <input
          type="text"
          className="form-control col"
          placeholder="Roll Number"
          name="rollno"
          onChange={onKeyChange}
        />
      </div>
      <table className="table">
        <thead>
          <tr>
            <th scope="col">#</th>
            <th scope="col">Name</th>
            <th scope="col">Email</th>
            <th scope="col">Roll no</th>
            <th scope="col">Action</th>
          </tr>
        </thead>
        <tbody>
          {userForm.map((user, index) => {
            return (
              <tr key={index}>
                <th scope="row">{user._id}</th>
                <td>{user.name}</td>
                <td>{user.email}</td>
                <td>@{user.rollno}</td>
                <td>
                  <Link
                    className="btn btn-primary btn-sm me-2"
                    to={"/edit-student/" + user._id}
                  >
                    Edit
                  </Link>
                  <button
                    className="btn btn-danger btn-sm"
                    onClick={() => deleteStudent(user._id)}
                  >
                    Delete
                  </button>
                </td>
              </tr>
            );
          })}
        </tbody>
      </table>
    </div>
  );
}

export default StudentList;
